import React from 'react'
import ReactDOM from 'react-dom/client'
import './index.css'

function Square(props) {
    return (
      <button className="square" onClick={props.onClick}>
        {props.value}
      </button>
    );
  }
  
  class Board extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        squares: Array(100).fill(0),
        firstClick: false
      };
    }

    createMines(first) {
        var mines = [];
        while(mines.length < 10){
            var r = Math.floor(Math.random() * 100) + 1;
            if(mines.indexOf(r) === -1 && r !== first) mines.push(r);
        }
        for (let i = 0; i < mines.length; i++) {
            this.state.squares[mines[i]] = 'X';
        }
    }

    createNums() {
        let squares = this.state.squares.slice()
        for (let i = 0; i< squares.length; i++) {
            if (squares[i] === 'X') {
                console.log("here")
                let nums = [i+1, i-1, i-10-1, i-10+1, i+10-1, i+10+1, i-10, i+10]
                for (let j = 0; j<nums.length && nums[j]>0 ; j++) {
                    squares[nums[j]] = squares[nums[j]]+1
                }
            }
        }
        this.setState({
            squares: squares,
            firstClick: true,
        })
    }
  
    handleClick(i) {
      if (!this.state.firstClick) {
        this.createMines(i)
        this.createNums()
      }
      const squares = this.state.squares.slice();
      if (calculateWinner(squares) || squares[i]) {
        return;
      }
      this.setState({
        squares: squares,
        xIsNext: !this.state.xIsNext,
      });
    }
  
    renderSquare(i) {
      return (
        <Square
          value={this.state.squares[i]}
          onClick={() => this.handleClick(i)}
        />
      );
    }
  
    render() {
      
      const winner = calculateWinner(this.state.squares);
      let status;
      if (winner) {
        status = 'Winner: ' + winner;
      } else {
        status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
      }
      
      const rows = [];
      for (let i = 0; i < 10; i++) {
        const cols = [];
        for (let j = 0; j < 10; j++) {
          cols.push(this.renderSquare(i * 10 + j));
        }
        rows.push(<div className="board-row">{cols}</div>);
      }
      return (
        rows
      );
    }
  }
  
  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }
  
  // ========================================
  
  const root = ReactDOM.createRoot(document.getElementById("root"));
  root.render(<Game />);
  
  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }
