import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

function Square(props) {
    return (
        <button className="square" onClick={props.onClick}>
            {props.value}
        </button>
    );
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array.from({ length: 10 }, () => Array(10).fill({ value: 0, flag: false, open: false })),
            firstClick: false
        };
    }

    toXY(num) {
        return [num % 10, Math.floor(num / 10)];
    }

    createMines(first) {
        var mines = [];
        while (mines.length < 10) {
            var r = Math.floor(Math.random() * 100);
            if (mines.indexOf(r) === -1 && r !== first) mines.push(r);
        }
        this.setState(prevState => {
            const squares = prevState.squares.map(row => row.slice());
            for (let i = 0; i < mines.length; i++) {
                let [x, y] = this.toXY(mines[i]);
                squares[y][x] = { ...squares[y][x], value: 'X' };
            }
            return { squares };
        }, this.createNums);
    }

    createNums() {
        this.setState(prevState => {
            const squares = prevState.squares.map(row => row.slice());
            for (let x = 0; x < 10; x++) {
                for (let y = 0; y < 10; y++) {
                    if (squares[y][x].value === 'X') {
                        const cells = [[x - 1, y - 1], [x - 1, y], [x - 1, y + 1], [x, y - 1], [x, y + 1], [x + 1, y - 1], [x + 1, y], [x + 1, y + 1]];
                        for (let j = 0; j < cells.length; j++) {
                            let [xt, yt] = cells[j];
                            if (xt >= 0 && xt < 10 && yt >= 0 && yt < 10 && squares[yt][xt].value !== 'X') {
                                squares[yt][xt] = { ...squares[yt][xt], value: squares[yt][xt].value + 1 };
                            }
                        }
                    }
                }
            }
            return { squares };
        });
    }

    handleClick(x, y) {
        if (!this.state.firstClick) {
            this.createMines(y * 10 + x);
            this.setState({ firstClick: true });
        }
    }

    renderSquare(x, y) {
        return (
            <Square
                key={`${x}-${y}`}
                value={this.state.squares[y][x].value}
                onClick={() => this.handleClick(x, y)}
            />
        );
    }

    render() {
        const rows = [];
        for (let i = 0; i < 10; i++) {
            const cols = [];
            for (let j = 0; j < 10; j++) {
                cols.push(this.renderSquare(j, i));
            }
            rows.push(
                <div key={i} className="board-row">
                    {cols}
                </div>
            );
        }
        return <div>{rows}</div>;
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
                <div className="game-info">
                    <div>{/* status */}</div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);
