import React, {useState} from "react";
import Modal from 'react-modal';
import Slider from 'react-slider'
import { useEffect } from 'react'; 

function DropdownMenu({ options, value, onChange}) {
    const [isOpen, setIsOpen] = useState(false);

    const handleClick = () => {
        setIsOpen(!isOpen);
    };

    const handleOptionClick = (option) => {
        onChange(option);
        setIsOpen(false);
    };

    return (
        <div className="dropdown">
            <div className="dropdown-button" onClick={handleClick}>
                {value}
            </div>
            {isOpen && (
                <div className="dropdown-menu">
                    {options.map((option) => (
                        <div
                            className="dropdown-option"
                            key={option}
                            onClick={() => handleOptionClick(option)}
                        >
                            {option}
                        </div>
                    ))}
                </div>
            )}
        </div>
    )
}

function Configurations ( {opened, onSave, sliderColor, onClosed} ) {
    const [color, setColor] = useState('#228B22')
    const [speed, setSpeed] = useState(0.50)
    const [size, setSize] = useState('Small')

    function closeAndSave() {
        onSave(color, speed, size)
        onClosed()
    }
    const colors = ['#228B22', '#7F1734', '#87CEEB', '#FFFF00', '#E6E6FA', '#CC5500']
    return (
        <Modal isOpen={opened} className="config-menu" onRequestClose={onClosed} contentLabel="Configurations" style={{overlay: {backgroundColor: 'rgba(0, 0, 0, 0.6)', zIndex: 5}}}>
            <div className="config-menu-inner">
            <h4>Configurations</h4>
            <p className='config-text'>Choose color:</p>
            <div className='colors-container'>
                {colors.map((c, i) => <div className='color-choice' key={c} style={{backgroundColor: c, borderColor: sliderColor===c ? null : 'white'}} onClick={() => setColor(c)}></div>)}
            </div>
            <p className='config-text'>Animation speed:</p>
            <Slider
                className='slider'
                value={speed}
                color={sliderColor}
                onChange={setSpeed}
                orientation="horizontal"
                min={0.00}
                max={1.00}
                step={0.01}
            ></Slider>
            <p className='config-text'>Board Size:</p>
            <div className="size-container">
                <DropdownMenu options={['Small', 'Medium', 'Large', 'Extra Large']} value={size} onChange={setSize}></DropdownMenu>
            </div>
            <button className='config-save' onClick={closeAndSave}>Save</button>
            </div>
        </Modal>
    )
}


function Timer() {
    const [minutes, setMinutes] = useState(0);
    const [seconds, setSeconds] = useState(0);
  
    useEffect(() => {
      const intervalId = setInterval(() => {
        if (seconds === 59) {
          setMinutes(minutes => minutes + 1);
          setSeconds(0);
        } else {
          setSeconds(seconds => seconds + 1);
        }
      }, 1000); // Update every 1 second
  
      return () => clearInterval(intervalId); // Cleanup function
    }, []); // Empty dependency array: run effect only once on mount
  
    const formatTime = (value) => {
      return value.toString().padStart(2, '0'); // Pad with leading zeros
    };
  
    return (
      <div>
        <Modal></Modal>
          {formatTime(minutes)}:{formatTime(seconds)}
      </div>
    );
  }

export {DropdownMenu, Configurations, Timer}
