import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import flag from './flag.svg'

function Square(props) {
    const style = {
        backgroundColor: props.open ? 'lightgrey' : 'darkgrey',
        color: props.value === 'X' ? 'red' : 'black'
    };
    let val = props.value
    if (props.value === 0) {
        val = ''
    }
    let img = props.flag ? <img src={flag}></img> : null;
    const handler = (e) => {
        e.preventDefault();
        props.onContextMenu()
    }
    return (
        <button className="square" style={style} onClick={props.onClick} onContextMenu={handler} >
            {img}
            {props.open ? val : ''}
        </button>
    );
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array.from({ length: 10 }, () => Array(10).fill({ value: 0, flag: false, open: false })),
            firstClick: false
        };
    }

    toXY(num) {
        return [num % 10, Math.floor(num / 10)];
    }

    getNeighbours(x, y) {
        var cells = [[x - 1, y - 1], [x - 1, y], [x - 1, y + 1], [x, y - 1], [x, y + 1], [x + 1, y - 1], [x + 1, y], [x + 1, y + 1]];
        return cells.filter(cell => cell[0] >= 0 && cell[0] < 10 && cell[1] >= 0 && cell[1] < 10);
    }

    createMines(first) {
        var mines = [];
        while (mines.length < 15) {
            var r = Math.floor(Math.random() * 100);
            if (mines.indexOf(r) === -1 && r !== first) mines.push(r);
        }
        this.setState(prevState => {
            const squares = prevState.squares.map(row => row.slice());
            for (let i = 0; i < mines.length; i++) {
                let [x, y] = this.toXY(mines[i]);
                squares[y][x] = { ...squares[y][x], value: 'X' };
            }
            return { squares };
        }, this.createNums);
    }

    createNums() {
        this.setState(prevState => {
            const squares = prevState.squares.map(row => row.slice());
            for (let x = 0; x < 10; x++) {
                for (let y = 0; y < 10; y++) {
                    if (squares[y][x].value === 'X') {
                        let cells = this.getNeighbours(x, y);
                        for (let j = 0; j < cells.length; j++) {
                            let [xt, yt] = cells[j];
                            if (squares[yt][xt].value !== 'X') {
                                squares[yt][xt] = { ...squares[yt][xt], value: squares[yt][xt].value + 1 };
                            }
                        }
                    }
                }
            }
            return { squares };
        });
    }

    handleClick(x, y) {
        if (!this.state.firstClick) {
            this.createMines(y * 10 + x);
            this.setState({ firstClick: true }, () => this.handleClick(x, y));
            return;
        }
        if (!this.state.squares[y][x].open && !this.state.squares[y][x].flag) {
            this.setState(prevState => {
                const squares = prevState.squares.map(row => row.slice());
                squares[y][x] = { ...squares[y][x], open: true };
                return { squares };
            }, () => {
                if (this.state.squares[y][x].value === 0) {
                    this.openSquares(x, y);
                }
            });
        } else if (this.state.squares[y][x].open ) {
            console.log(this.openFlagNeighbours(x, y))
            this.openFlagNeighbours(x, y);
        }
    }

    handleRightClick(x, y) {
        this.setState(prevState => {
            const squares = prevState.squares.map(row => row.slice());
            const flagged = !squares[y][x].flag
            squares[y][x] = { ...squares[y][x], flag: squares[y][x].open ? !flagged : flagged };
            return { squares };
        });
        return false;
    }

    openFlagNeighbours(x, y) {
        const val = this.state.squares[y][x].value;
        var cells = this.getNeighbours(x, y);
        let flagnums = 0;
        
        cells.forEach(cell => {
            if (this.state.squares[cell[1]][cell[0]].flag) {
                flagnums++;
            }
        })
        console.log(flagnums, val);
        if (flagnums === val) {
            cells.forEach(cell => {
                if (!this.state.squares[cell[1]][cell[0]].open && !this.state.squares[cell[1]][cell[0]].flag) {
                    this.handleClick(cell[0], cell[1]);
                }
            })
        }
    }

    openSquares(x, y) {
        const cells = this.getNeighbours(x, y);
        for (let j = 0; j < cells.length; j++) {
            let [xt, yt] = cells[j];
            if (!this.state.squares[yt][xt].open && !this.state.squares[yt][xt].flag && this.state.squares[yt][xt].value !== 'X') {
                this.setState(prevState => {
                    const squares = prevState.squares.map(row => row.slice());
                    squares[yt][xt] = { ...squares[yt][xt], open: true };
                    return { squares };
                }, () => {
                    if (this.state.squares[yt][xt].value === 0) {
                        this.openSquares(xt, yt);
                    }
                });
            }
        }
    }

    renderSquare(x, y) {
        const square = this.state.squares[y][x];
        return (
            <Square
                key={`${x}-${y}`}
                value={square.value}
                flag={square.flag}
                open={square.open}
                onClick={() => this.handleClick(x, y)}
                onContextMenu={() => this.handleRightClick(x, y)}
            />
        );
    }

    render() {
        const rows = [];
        for (let i = 0; i < 10; i++) {
            const cols = [];
            for (let j = 0; j < 10; j++) {
                cols.push(this.renderSquare(j, i));
            }
            rows.push(
                <div key={i} className="board-row">
                    {cols}
                </div>
            );
        }
        let status = "Ongoing"
        if (checkWin(this.state.squares)) {
            status = "You won!";
        }
        return <div>{rows} {status}</div>;
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
                <div className="game-info">
                    <div>{/* status */}</div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}


function checkWin(squares) {
    for (let i = 0; i < 10; i++) {
        for (let j = 0; j < 10; j++) {
            if (!squares[i][j].open && squares[i][j].value !== 'X') {
                return false;
            }
        }
    }
    return true;
}
// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);
